mapper.select:
SELECT
IFNULL(SUM(AL.point), 0) AS `point`
FROM articleLike AS AL
WHERE
AL.articleId = #{id}
AND AL.memberId = #{memberId}

mapper.update:
UPDATE article
SET hit = hit + 1
WHERE id = #{id}

UPDATE article
title=#{title}, body=#{body},
WHERE id = #{id}

mapper.select:
SELECT A.*
FROM article AS A
WHERE A.id = #{id}
